function(pageData, pageUrl) {
	var retVal = {};

	var params = pageUrl.match(/^http:\/\/www\.adultism\.com\/pi(\d+)\/#!(.+)/);

	var newUrl = "http://www.adultism.com/dn_postimage.php?p=" + params[1] + "&m=" + params[2];
	var req = new XMLHttpRequest();
	req.open("GET", newUrl, false);
	req.setRequestHeader("Referer", pageUrl);
	req.send(null);

	if (req.status != 200) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		return retVal;
		}

	var newPage = req.responseText;
	var iUrl = newPage.match(/<img src="(.+?)"/);

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
		}

	return retVal;
	}

