function(pageData, pageUrl) {
	var rUrl = "http://animexx.onlinewelten.com"
	var reg = new RegExp("(foto|img)_holder(.|\r|\n)+?\/div(.|\r|\n)+?\/div");	
	if (reg.test(pageData)) {
		var pagePart = pageData.match("(foto|img)_holder(?:.|\r|\n)+?\/div(?:.|\r|\n)+?\/div")[0];
		var id = pagePart.match(/data-foto_id="(.+?)"/)[1];
		var tpl = pagePart.match(/data-foto_link_tpl="(.+?)#foto#\/"/)[1];
		var urlPart2 = "?grossansicht_2226478b=1"
		var urlto = rUrl+tpl+id+"/"+urlPart2;			
		var req = new XMLHttpRequest();			
		req.open("POST", urlto, false);
		req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");		
		req.send("");		
		iUrl = (req.responseText).match(/img.+src="(.+?)"/)[1];
		return {imgUrl: iUrl, status: "OK"}	
	}				
	return {imgUrl: null, status: "ABORT"}	
}

