function(pageData, pageUrl) {
	var theId = "full";
	var retVal = {};
	var searchPat = new RegExp("<.+id(?:\\s+)?=(?:\\s+)?(\"|')?" + theId + "\\1.+?>");
	var tmpMatch = pageData.match(searchPat);
	if (tmpMatch) {
		tmpMatch = tmpMatch[0];
		var the_url = tmpMatch.match(/src(?:\s+)?=(?:\s+)?("|')?(.+?)\1/);
		if (the_url) the_url = the_url[2];
		}
	if(!the_url) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = the_url;
		retVal.status = "OK";
		}
	return retVal;
}

function(pageData, pageUrl) {
	var PostData = pageData.match(/(?=<Form\b)((.|\r|\n)+?)<\/Form>/)[1].match(/<input.+?\/?>/g);
	PostData = PostData.map(function(input){return input.match(/name=('|")(.+?)\1/)[2] + '=' + input.match(/value=('|")(.+?)\1/)[2]}).join('&');	

	var req = new XMLHttpRequest();
	req.open("POST", pageUrl, false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	req.send(PostData);	

	var iUrl = (req.responseText).match(/src=("|')(http:\/\/[^/]+\/(img|i)\/.+?)\1.+alt=("|')(.+\.jpe?g)\4/i);
	return iUrl ? {imgUrl: iUrl[2], status: "OK"} : {imgUrl: null, status: "ABORT"}
}

