function(pageData, pageUrl) {
	var retVal = {};

	var theId = "redirectframe";
	var someMatch = new RegExp("<iframe.+?id=('|\")" + theId + "\\1.+?>");
	var frameElem = pageData.match(someMatch);

	if (frameElem) {
		var srcMatch = frameElem.toString().match(/src=("|')(.+?)\1/);
		if (srcMatch) var newPageUrl = srcMatch[2];
		}

	if (!newPageUrl) {
		retVal.status = "ABORT";
		retVal.imgUrl = null;
		}
	else {
		retVal.status = "REQUEUE";
		retVal.imgUrl = newPageUrl;
		}
	return retVal;
	}

