function(pageData, pageUrl) {
	var retVal = {};

	if (pageData.match(/Continue To Your Image/)) {
		retVal.imgUrl = pageUrl;
		retVal.status = "REQUEUE";
		}
	else { 
		var theId = "image";
		var searchPat = new RegExp("<.+id(?:\\s+)?=(?:\\s+)?(\"|')?" + theId + "\\1.+?>");
		var tmpMatch = pageData.match(searchPat);
		if (tmpMatch) {
			tmpMatch = tmpMatch[0];
			var iUrl = tmpMatch.match(/src(?:\s+)?=(?:\s+)?("|')?(.+?)\1/);
			if (iUrl) iUrl = iUrl[2];
			}

		if (!iUrl) {
			retVal.imgUrl = null;
			retVal.status = "ABORT";
			}
		else {
			retVal.imgUrl = iUrl;
			retVal.status = "OK";
			}
		}		

	return retVal;
	}

function(pageData, pageUrl) {
	 var retVal = {imgUrl: null, status: "ABORT"};

	 var req = new XMLHttpRequest();
	 req.open("POST", pageUrl, false);
	 req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	 req.send("");

	 var someData = req.responseText;
	 iUrl = someData.match(/<img.+?id="image".+?>/g).toString();

	 if (iUrl) {
		 retVal.imgUrl = iUrl.match(/src="([^"]+)"/)[1];
		 retVal.fileName = someData.match(/<b>(\w+\.jpg)<\/b>/)[1];
		 retVal.status = "OK";
	 }

	 return retVal;
}

