function(pageData, pageUrl) {
	 var retVal = new Object();

	 var iUrl = pageData.match(/<json>(.+)<\/json>/);
	 iUrl = unescape(iUrl[1]);
	 iUrl = iUrl.match(/"(.+)"/);
	 iUrl = iUrl[1].replace(/\\/gi, "");

	 if (!iUrl) {
		 retVal.imgUrl = null;
		 retVal.status = "ABORT";
