function(pageData, pageUrl) {
    var retVal = {};
    var assCleaner = pageData.match(/var url_image_path = '(.+)';\s*/);   
    if (!assCleaner) {
		assCleaner = pageData.match(/<link.+rel=('|")image_src\1.+href=('|")(.+?)\2.+>/);
		if (!assCleaner) assCleaner = pageData.match(/<img.+id=('|")main_image\1.+src=('|")(.+?)\2.+>/);
		if (!assCleaner) {
			retVal.imgUrl = null;
			retVal.status = "ABORT";
			}
		else {
			retVal.imgUrl = assCleaner[3];
			retVal.status = "OK";
			}
        }
    else {
        retVal.imgUrl = pageUrl.match(/http:\/\/img\d+.imageshack.us\//) + assCleaner[1];
        retVal.status = "OK";
        }
    return retVal;
    }

function(pageData, pageUrl) {
   var retVal = {};

   var IDandName = pageUrl.match(/\/(\w+)\/([^.]+\..{3,4})\.html$/);
   var theID = IDandName[1];
   var fileName = IDandName[2];
   var SearchPatt = new RegExp("<img src=\"(.+?\/"+ theID +"\\..{3,4})\".+?>");
   var iUrl = pageData.match(SearchPatt);

   if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
   else {
		retVal.imgUrl = iUrl[1];
		retVal.fileName = fileName;
		retVal.status = "OK";
		}

   return retVal;
   }

