function(pageData, pageUrl) {
	var retVal = {};

	if (pageData.match(/View Image/)) {
		retVal.imgUrl = pageUrl + '&i=1';
		retVal.status = "RETRY";
		}
	else if (pageData.match(/This image does not exist on this server/)) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		// use the following 4 lines if you are using the id attribute to find the image source.
		// just modify the "theId" variable to reflect the proper ID
		var theId = "thepic";

		var searchPat = new RegExp("<.+id(?:\\s+)?=(?:\\s+)?(\"|')?" + theId + "\\1(?:.|\\s)+?>");
		var tmpMatch = pageData.match(searchPat);
		if (tmpMatch) {
			tmpMatch = tmpMatch[0];
			var the_url = tmpMatch.match(/src(?:\s+)?=(?:\s+)?("|')?(.+?)\1/i);
			if (the_url) the_url = the_url[2];
			}

		if (!the_url) {
			retVal.imgUrl = null;
			retVal.status = "RETRY";
			}
		else {
			retVal.imgUrl = the_url;
			retVal.status = "OK";
			}
		}

	return retVal;
	}

