function(pageData, pageUrl) {
	var HostUrl = "http://imgdragon.com/image.php"

	var PostData = pageData.match(/<form(.|\r|\n)+?form>/)[0].match(/<input.+?\/?>/g);;
	PostData = PostData.map(
	function(input)
	{	var sPart1 = input.match(/name=('|")(.+?)\1/)[2];
	   	var sPart2 = '';
	   	if(!new RegExp(/value=('|")(.+?)\1/).test(input)) {
	     		sPart2 = '';
	   	} else {
	     		sPart2 =  input.match(/value=('|")(.+?)\1/)[2];
	   	}
		return sPart1  + '=' + sPart2;
	}).join('&');

	var req = new XMLHttpRequest();
	req.open("POST", HostUrl, false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	req.send(PostData);

	var PostData = req.responseText.match(/<Form(.|\r|\n)+?Form>/)[0].match(/<input.+?\/?>/g);
	PostData = PostData.map(function(input){return input.match(/name=('|")(.+?)\1/)[2] + '=' + input.match(/value=('|")(.+?)\1/)[2]}).join('&');

	req.open("POST", HostUrl, false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	req.send(PostData);

	var iUrl = (req.responseText).match(/img src="(.+?)".+class="pic"/);
	return iUrl ? {imgUrl: iUrl[1], fileName: iUrl[1].replace(/\.jpe?g/i, ".jpg"), status: "OK"} : {imgUrl: null, status: "ABORT"}	
}

