function(pageData, pageUrl) {
	var pLoc =pageData.match(/window.location = "(.+?)"/)
	var req = new XMLHttpRequest();
	req.open("POST", pLoc[1], false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	req.send("");
	var PostData = (req.responseText).match(/\.prepend\('(?=<Form\b)(.+?)<\/Form>'/)[1].match(/<input.+?\/?>/g);
	PostData = PostData.map(function(input){return input.match(/name=('|")(.+?)\1/)[2] + '=' + input.match(/value=('|")(.+?)\1/)[2]}).join('&');
	req.open("POST", pLoc[1], false);
	req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
	req.send(PostData);
	var iUrl = (req.responseText).match(/src=("|')(http:\/\/[^/]+\/img\/.+?\.jpe?g)\1/i);
	return iUrl ? {imgUrl: iUrl[2], status: "OK"} : {imgUrl: null, status: "ABORT"}
}

function(pageData, pageUrl) {
	var retVal = {imgUrl: null, status: "ABORT"};
	var iUrl = null;

	var id = pageUrl.match(/(\w+)\/[\w-]+\.jpe?g\.html$/i);

	if (id) {
		var req = new XMLHttpRequest();
		req.open("POST", pageUrl, false);
		req.setRequestHeader("Content-Type", "application/x-www-form-urlencoded; charset=UTF-8");
		req.send("op=view&id="+ id[1] +"&pre=1&next=Continue to image...");

		var someData = req.responseText;
		iUrl= someData.match(/(?:href|src)=('|")(http:\/\/[^/]+\/i\/\d+\/.+?(?!_t)\w{2}\.jpe?g)\1/);
		if (iUrl) {
			retVal.imgUrl = iUrl[2];
			retVal.status = "OK";
			}
		}
	return retVal;
}

