function(pageData, pageUrl) {
	var iUrl = pageData.match(/<link rel="image_src" href="([^"]+)"/);
	if (!iUrl) iUrl = pageData.match(/<meta property="og:image" content="([^"]+)"/);
	if (!iUrl) iUrl = pageData.match(/<a href="(.*\/\/i\.imgur\.com\/[^"]+)"/);
	if (!iUrl) iUrl = pageData.match(/<img src="(.*\/\/i\.imgur\.com\/[^"]+)"/);
	return iUrl ? {imgUrl: iUrl[1], status: "OK"} : {imgUrl: null, status: "ABORT"};
}

