function(pageData, pageUrl) {
	var retVal = {};

	var srcPat = "http:\\/\\/static[0-9]+\\.myincesthentai\\.com\\/[0-9]+\\/[0-9]+\\/.+_(.+?|)\\.(jpg|jpeg|png)";

	var searchPat = new RegExp("<a.+href(?:\\s+)?=(?:\\s+)?(\"|')?(" + srcPat + ")\\1.*?>");
	var tmpMatch = pageData.match(searchPat);

	if (tmpMatch) var the_url = tmpMatch[2];

	if (!the_url) {
		retVal.status = "ABORT";
		retVal.imgUrl = null;
	}
	else {
		retVal.status = "OK";
		retVal.imgUrl = the_url;
	}
	var regex = new RegExp("albums\\/.+/(\\d+)");
	var imgs= pageUrl.match(regex); 
	try {
		retVal.fileName = imgs[1] + ".jpg";
	}
	catch(e) { 
		retVal.fileName = Math.random().toString().substring(2) + ".jpg";
	}

	return retVal;
}

