	var tmpMatch = pageData.match(/<div class="image"(?:.|\s)+?<img src="(.+?)"/);
 	if (tmpMatch) var the_url = tmpMatch[1];

	if (!the_url) {
		retVal.status = "ABORT";
		retVal.imgUrl = null;
		}
	else {
		retVal.status = "OK";
		retVal.imgUrl = the_url;
		}
	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = {};

	var newPageUrl = pageData.match(/linkDestUrl = '(.+?)'/);

	if (!newPageUrl) {
		retVal.status = "ABORT";
		retVal.imgUrl = null;
		}
	else {
		retVal.status = "REQUEUE";
		retVal.imgUrl = newPageUrl[1];
		}

	return retVal;
	}

function(pageData, pageUrl) {
	var iUrl;

	if (!pageUrl.match(/\/full\//)) {
		iUrl = pageData.match(/<a href='(.+?)'>full size/);
		if (iUrl) return {imgUrl: iUrl[1], status: "REQUEUE"}
	}

	iUrl = pageData.match(/src=('|")(http:\/\/[^/]+\/.+?\.jpe?g)\1/i);
	return (iUrl) ? {imgUrl: iUrl[2], status: "OK"} : {imgUrl: null, status: "ABORT"}
}

