function(pageData, pageUrl) {
	var retVal = {};

	var iUrl = null;
	if (pageUrl.match(/full_image\/$/)) {
		iUrl = pageData.match(/<img src="(.+?)" onClick="window.close\(\);"/);
		}
	else {
		var newPageUrl = pageData.match(/<a href="[^"]+full_image\/"[^>]+onclick="open_window_prot/);
		if (newPageUrl) {
			retVal.imgUrl = pageUrl + "full_image/";
			retVal.status = "REQUEUE";
			return retVal;
			}

		iUrl = pageData.match(/<img src="(.+?)"[^>]+id="photo_preview_image"/);
		}

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
		}

	return retVal;
	}

