}

function(pageData, pageUrl) {
	var retVal = {};

	var theId = "highres";

	var searchPat = new RegExp("href=[\"|']([^\"|']*)\"[^<]*>Original image");
	var tmpMatch = pageData.match(searchPat);
	if (tmpMatch) {
		var the_url = tmpMatch[1];
		}

	if(!the_url) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = the_url;
		retVal.status = "OK";
		}

	return retVal;
	}

function(pageData, pageUrl) {
	var retVal = {};

	var theId = "highres";

	var searchPat = new RegExp("<.+id(?:\\s+)?=(?:\\s+)?(\"|')?" + theId + "\\1.+?>");
	var tmpMatch = pageData.match(searchPat);
	if (tmpMatch) {
		tmpMatch = tmpMatch[0];
		var the_url = tmpMatch.match(/href(?:\s+)?=(?:\s+)?("|')?(.+?)\1/);
		if (the_url) the_url = the_url[2];
		}
	else {
		theId = "image";
		searchPat = new RegExp("<.+id(?:\\s+)?=(?:\\s+)?(\"|')?" + theId + "\\1.+?>");
		tmpMatch = pageData.match(searchPat);
		if (tmpMatch) {
			tmpMatch = tmpMatch[0];
			the_url = tmpMatch.match(/src(?:\s+)?=(?:\s+)?("|')?(.+?)\1/);
			if (the_url) the_url = the_url[2];
			}
		}

	if(!the_url) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		retVal.imgUrl = the_url;
		retVal.status = "OK";
		}

	return retVal;
	}

