function(pageData, pageUrl) {
	var retVal = {};

	if (pageData.match(/gal\/preview/))
		{
		var srcPat = "modules\\.php\\?name=gal&file=photo&a=\\d+&p=\\d+&f=(?:\\d+)";

		var searchPat = new RegExp("<a.+href(?:\\s+)?=(?:\\s+)?(\"|')?(" + srcPat + ")\\1(?:.+?)?>");
		var the_url = pageData.match(searchPat);
		if (the_url) the_url = the_url[2];

		the_url = pageUrl + '&f=' + the_url;

		retVal.status = "RETRY";
		retVal.imgUrl = the_url;
		}

	else
		{
		var img_tags = pageData.match(/<img.+src(?:\s+)?=(?:\s+)?(\"|')?.+?\1(?:.+?)?>/g); 

		for (i=0; i < img_tags.length; i++) {
			if (img_tags[i].match(/gal\/albums/)) { 
				var the_url = img_tags[i].match(/src(?:\s+)?=(?:\s+)?(\"|')?(.+?)\1/);
				if (the_url) the_url = the_url[2];
				}
			}

		if (!the_url) {
			retVal.imgUrl = null;
			retVal.status = "ABORT";
			}
		else {
			retVal.imgUrl = the_url;
			retVal.status = "OK";
			}
		}

	return retVal;
	}

