function(pageData, pageUrl) {
	var retVal = {};

	var photo_div = pageData.match(/<div class="photoWrapper"(?:.|\s)+?<\/div>/);
	if (photo_div) {
		photo_div = photo_div[0];
		var iUrl = photo_div.match(/<a href="([^"]+)".+?<img src="([^"]+)"/);
		if (!iUrl) var directUrl = photo_div.match(/<img src="(.+?)"/);
	}

	if (directUrl) {
		retVal.imgUrl = directUrl[1];
		retVal.status = "OK";
		return retVal;	
	}

	if (!iUrl) {
		retVal.imgUrl = null;
		retVal.status = "ABORT";
		}
	else {
		if (iUrl[1].search(/^http:\/\/.+\.media\.tumblr\.com\/.+/) >= 0 ||
			iUrl[1].search(/^http:\/\/www\.theselittlepix\.com\/photo\/\d+\/\d+\/.+/) >= 0) {
			retVal.imgUrl = iUrl[1];
			retVal.status = "OK";
			}
		else {	
			retVal.imgUrl = iUrl[2];
			retVal.status = "OK";
			}
		}

	return retVal;
	}

