function(pageData, pageUrl) {
	var retVal = {imgUrl: null, status: "ABORT"};

	var iUrl = pageData.match(/<meta property="og:image" content="([^"]+)"/);
	if (!iUrl) iUrl = pageData.match(/src="(http:\/\/[^\/]+\.tumblr\.com\/[^"]*tumblr_[^"]+_1280\.\w{3,4})"/);
	if (!iUrl) iUrl = pageData.match(/src="(http:\/\/[^\/]+\.tumblr\.com\/[^"]*tumblr_[^"]+_500\.\w{3,4})"/);
	if (!iUrl) iUrl = pageData.match(/src="(http:\/\/[^\/]+\.tumblr\.com\/[^"]*tumblr_[^"]+_400\.\w{3,4})"/);
	if (!iUrl) iUrl = pageData.match(/href="(http:\/\/[^\/]+\.tumblr\.com\/photo\/\d+\/.+?)"/);
	if (!iUrl) iUrl = pageData.match(/href="(.+?)"[^>]+class="download image"/);
	if (iUrl) {
		retVal.imgUrl = iUrl[1];
		retVal.status = "OK";
	}

	return retVal;
}

function(pageData, pageUrl) {
